#include<stdio.h>
#include<string>
#include<gmp.h>

using namespace std;

int main(){
    string msg_decimal="";
    string msg_hex;

    mpz_t msg_tmp;
    mpz_init(msg_tmp);

    mpz_set_str(msg_tmp,msg_decimal.c_str(),10);
    mpz_get_str(msg_hex,16,msg_tmp);

    printf("msg_hex=%s\n",msg_hex);
    return 0;
}